import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Typer = () => {

    const [quote, setQuote] = useState('');
    const [input, setInput] = useState('');
    const [result, setResult] = useState('');
    const [score, setScore] = useState(0);
    const [style, setStyle] = useState('');
    const [time, setTime] = useState(0);
    const [chars, setChars] = useState(0); // sets total amount of characters a user has typed
    const [wpm, setWpm] = useState(0); // wpm = (chars / 5) * 60 / time
    const [start, setStart] = useState(0)

    if (start === true) {
        useEffect(
            () => {
                const timer = () => {
                    setTime(time + 1);
                }

                // if you want it to finish at some point
                // if (time >= 10) {
                //     return;
                // }
                const id = setInterval(timer, 1000);
                return () => clearInterval(id);
            },
            [time]
        );
    }

    const calculateWPM = () => {
        setWpm(Math.floor((chars / 5) * 60 / time) || 0);
    }

    // useEffect(() => {
    //     const interval = setInterval(() => {
    //         calculateWPM();
    //     }, 1000);

    //     return () => clearInterval(interval); // This represents the unmount function, in which you need to clear your interval to prevent memory leaks.
    // }, [])


    const fetchQuote = () => {
        axios("https://api.kanye.rest/").then((response) => {
            setQuote(response.data.quote.toLowerCase());
            console.log(quote);
        });
    }

    const _handleSubmit = (e) => {
        e.preventDefault();
        if (input === quote) {
            setScore(score + quote.length)
        } else {
            setScore(score)
        }
        setInput('');
        fetchQuote();
    }

    const _handleInput = (e) => {
        // console.log(e.target.value);
        start = true;
        setInput(e.target.value);
        const quoteByChar = quote.split('').slice(0, input.length).join('')
        if (input === quoteByChar) {
            setResult('nice')
            setStyle('green')
        } else {
            setResult('nahh')
            setStyle('red')
        }
        setChars(chars + 1);
        calculateWPM();
    }

    useEffect(fetchQuote, []);

    return (
        <div>
            <h3 className={ style }>{quote}</h3>
            <p>{ time }</p>
            <button onClick={ fetchQuote }>agane</button>
            <form onSubmit={ _handleSubmit }>
                <input type="textarea" required autoFocus onChange={ _handleInput } value={ input } />
                <input type="submit" value="go" />
            </form>
            <h3>{ result }</h3>
            <h3>wpm: { wpm }</h3>
            <h3>score: { score }</h3>
        </div>
    );
};

export default Typer;
