import React, { useState, useEffect } from 'react'
import { db } from '../firebase'
import { collection, getDocs } from 'firebase/firestore'
import { Table } from 'react-bootstrap'

export default function Scores() {
	const [scores, setScores] = useState([])
	const scoresCollectionRef = collection(db, "scores")

	useEffect(() => {
		// promise; async

		const getScores = async () => {
			const response = await getDocs(scoresCollectionRef);
			setScores(response.docs.map((doc) => ({ ...doc.data(), id: doc.id })))
		};

		getScores()
	}, [])
	// debugger
	return (
		<div>
			<Table>
				<thead>
					<tr>
						<th>ðŸ’¯</th>
						<th>Game</th>
						<th>Score</th>
						<th>Timer</th>
						<th>User</th>
					</tr>
				</thead>
				<tbody>
					<tr>
						<td>1</td>
						<td>{ scores[0] ? scores[0].game : "" }</td>  {/* TODO: map*/}
						<td>{ scores[0] ? scores[0].score : "" }</td>
						<td>{ scores[0] ? scores[0].timer : "" }</td>
						<td>{ scores[0] ? scores[0].user : "" }</td>
					</tr>
				</tbody>
			</Table>
		</div>
	)
}
