import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const Typer = () => {

    const [quote, setQuote] = useState('');
    const [input, setInput] = useState('');
    const [result, setResult] = useState('');
    const [score, setScore] = useState(0);
    const [style, setStyle] = useState('');



    // We can use useEffect so that when the component
    // mount the timer will start as soon as possible

    // We put empty array to act as componentDid
    // mount only
    useEffect(() => {
        clearTimer(getDeadTime());
    }, []);

    // Another way to call the clearTimer() to start
    // the countdown is via action event from the
    // button first we create function to be called
    // by the button
    const onClickReset = () => {
        clearTimer(getDeadTime());
    }

    const fetchQuote = () => {
        axios("https://api.kanye.rest/").then((response) => {
            setQuote(response.data.quote);
            console.log(quote);
        });
    }

    const _handleSubmit = (e) => {
        e.preventDefault();
        if (input === quote) {
            setScore(score + quote.length)
        } else {
            setScore(score)
        }
        setInput('');
        fetchQuote();
    }

    const _handleInput = (e) => {
        console.log(e.target.value);
        setInput(e.target.value);
        const quoteByChar = quote.split('').slice(0, input.length).join('')
        if (input === quoteByChar) {
            setResult('nice')
            setStyle('green')
        } else {
            setResult('nahh')
            setStyle('red')
        }
    }

    useEffect(fetchQuote, []);

    return (
        <div>
            <h3>{ timer }</h3>
            <h3 className={ style }>{quote}</h3>
            <button onClick={ fetchQuote }>agane</button>
            <form onSubmit={ _handleSubmit }>
                <input type="textarea" required autoFocus onChange={ _handleInput } value={ input } />
                <input type="submit" value="go" />
            </form>
            <h3>{ result }</h3>
            <h3>{ score }</h3>
        </div>
    );
};

export default Typer;
