import React, { useRef, useState } from 'react'
import { Container, Form, Button, Card, Alert } from 'react-bootstrap'
import { useAuth } from '../contexts/AuthContext'
import { Link, useNavigate } from "react-router-dom"

export default function UpdateProfile() {
	const emailRef = useRef()
	const passwordRef = useRef()
	const passwordConfirmRef = useRef()
	const { currentUser } = useAuth()
	const [error, setError] = useState('')
	const [loading, setLoading] = useState(false)
	const navigate = useNavigate()

	async function handleSubmit(e) {
		// e.preventDefault()

		// if (passwordRef.current.value !== passwordConfirmRef.current.value) {
		// 	return setError(`Passwords do not match`)
		// }

		// try {
		// 	setError('')
		// 	setLoading(true)
		// 	await signup(emailRef.current.value, passwordRef.current.value) 
		// 	navigate('/dashboard')
		// }	catch {
		// 	setError('Failed to create an account')
		// }

		// setLoading(false)
	}

	return (
		<>
			<Card>
				<Card.Body>
					<h2 className="text-center mb-4">Update Profile</h2>
					{error && <Alert variant="danger">{error}</Alert>}
					<Form onSubmit={handleSubmit}>
						<Form.Group id="email">
							<Form.Label>Email</Form.Label>
							<Form.Control type="email" ref={emailRef} required defaultValue={currentUser.email} />
						</Form.Group>
						<Form.Group id="password">
							<Form.Label>Password</Form.Label>
							<Form.Control type="password" ref={passwordRef} required placeholder="Leave blank to keep the same" />
						</Form.Group>
						<Form.Group id="password-confirm">
							<Form.Label>Password Confirmation</Form.Label>
							<Form.Control type="password" ref={passwordConfirmRef} required placeholder="Leave blank to keep the same" />
						</Form.Group>
						<br></br>
						<Button disabled={loading} className="w-100" type="submit">Update</Button>
					</Form>
				</Card.Body>
			</Card>
			<div className="w-100 text-center mt-2">
			Cancel<Link to="/login">Log In</Link>
			</div>
		</>
	)
}
